import Checkout from "./Checkout";
import {Link} from 'react-router-dom';

function App() {
  const [ingredients, setIngredients] = useState({
    basil: false,
    cheese: false,
    mushroom: false,
    olive: false,
    pineapple: false,
    tomato: false,
  });

  return (
    <>
      <Header />
      {/* <Router>
        <Switch>
          <Route exact path="/"> */}
          <Link to="/">
            <Customize
              ingredients={ingredients}
              setIngredients={setIngredients}
            /></Link>
          {/* </Route>
          <Route path="/checkout"> */}
            <Link to="/Checkout"> <Checkout ingredients={ingredients} /></Link>
          {/* </Route>
        </Switch>
      </Router> */}
    </>
  );
}

export default App;
