import React from "react";
import Base from "./images/PizzaBase.png";
import Basil from "./images/Basil.png";
import Mushroom from "./images/Mushroom.png";
import Olive from "./images/Olive.png";
import Pineapple from "./images/Pineapple.png";
import Pizza_logo from "./images/pizza logo.png";
import PizzaMan from "./images/PizzaMan.png";
import Tomato from "./images/Tomato.png";
import BaseCheese from "./images/BaseCheese.png";

const Customize = ({ ingredients, setIngredients }) => {
  //   const changeIngredients = (name) => {
  //     let newIngredients = JSON.parse(JSON.stringify(ingredients));
  //     newIngredients[name] = !newIngredients[name];
  //     setIngredients(newIngredients);
  //   };
  const onChange = (event, name) => {
    console.log(localStorage);
    let newIngredients = JSON.parse(JSON.stringify(ingredients));
    newIngredients[name] = event;
    setIngredients(newIngredients);
    localStorage.setItem("ingredients", JSON.stringify(newIngredients));
  };

  return (
    <>
      <div style={{ display: "flex" }}>
        {/* {JSON.stringify(ingredients)} */}
        <div style={{ flex: 1, padding: 40, border: "3px solid black" }}>
          <div style={{ maxHeight: 500, maxWidth: 500, position: "relative" }}>
            <img
              src={BaseCheese}
              alt="base"
              height="100%"
              width="100%"
              className="ingredients"
            />
            <img
              src={Basil}
              alt="base"
              height="100%"
              width="100%"
              className="ingredients"
            />
            <img
              src={Mushroom}
              alt="base"
              height="100%"
              width="100%"
              className="ingredients"
            />
            <img
              src={Olive}
              alt="base"
              height="100%"
              width="100%"
              className="ingredients"
            />
            <img
              src={Pineapple}
              alt="base"
              height="100%"
              width="100%"
              className="ingredients"
            />
            {/* <img src={Pizza_logo} alt="base" height="100%" width="100%"  className="ingredients"/> */}
            {/* <img src={PizzaMan} alt="base" height="100%" width="100%"  className="ingredients"/> */}
            <img
              src={Tomato}
              alt="base"
              height="100%"
              width="100%"
              className="ingredients"
            />
            <img src={Base} alt="base" height="100%" width="100%" />
          </div>
        </div>
        <div style={{ border: "3px solid black", flex: 1 }}>
          <h1 className="heading">Ingredients</h1>
          <div className="labels">
          <label className="container-checkbox">
            Pineapple
            <input
              type="checkbox"
              checked={ingredients["pineapple"]}
              onChange={(event) =>
                onChange(event.currentTarget.checked, "pineapple")
              }
            />
            <span className="checkmark"></span>
          </label>
          <label className="container-checkbox">
            Basil
            <input
              type="checkbox"
              checked={ingredients["basil"]}
              onChange={(event) =>
                onChange(event.currentTarget.checked, "basil")
              }
            />
            <span className="checkmark"></span>
          </label>
          <label className="container-checkbox">
            Olive
            <input
              type="checkbox"
              checked={ingredients["olive"]}
              onChange={(event) =>
                onChange(event.currentTarget.checked, "olive")
              }
            />
            <span className="checkmark"></span>
          </label>
          <label className="container-checkbox">
            Tomato
            <input
              type="checkbox"
              checked={ingredients["tomato"]}
              onChange={(event) =>
                onChange(event.currentTarget.checked, "tomato")
              }
            />
            <span className="checkmark"></span>
          </label>
          <label className="container-checkbox">
            Mushroom
            <input
              type="checkbox"
              checked={ingredients["mushroom"]}
              onChange={(event) =>
                onChange(event.currentTarget.checked, "mushroom")
              }
            />
            <span className="checkmark"></span>
          </label>
          <label className="container-checkbox">
            Cheese
            <input
              type="checkbox"
              checked={ingredients["cheese"]}
              onChange={(event) =>
                onChange(event.currentTarget.checked, "cheese")
              }
            />
            <span className="checkmark"></span>
          </label>
          </div>
        </div>
      </div>
    </>
  );
};

export default Customize;
